// src/components/Results.js

import React, { useRef } from 'react';
import { Line } from 'react-chartjs-2';
import { calculateFutureValue } from '../utils/calculations';
import CountUp from 'react-countup';
import { toPng } from 'html-to-image';
import { SaveAsIcon } from '@heroicons/react/outline'; // Ensure this icon exists or use a suitable alternative

// Import Chart.js and required components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function Results({ entry }) {
  const { currentAge, retirementAge, spendingAmount, rateOfReturn } = entry;
  const years = retirementAge - currentAge;

  // Calculate the final future value
  const finalValue = calculateFutureValue(spendingAmount, rateOfReturn, years);
  const formattedFinalValue = finalValue.toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 });

  // Generate data points for each year
  const labels = Array.from({ length: years + 1 }, (_, i) => currentAge + i);
  const dataPoints = Array.from({ length: years + 1 }, (_, i) =>
    Math.round(calculateFutureValue(spendingAmount, rateOfReturn, i))
  );

  const data = {
    labels: labels,
    datasets: [
      {
        label: `Growth at ${(rateOfReturn * 100).toFixed(2)}% Annual Return`,
        data: dataPoints,
        fill: false,
        backgroundColor: '#10B981', // Emerald-500
        borderColor: '#10B981',
        tension: 0.3, // Smoother curves
        pointRadius: 4,
        pointHoverRadius: 6,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false, // Allows the chart to adjust its height
    plugins: {
      legend: {
        position: 'top',
        labels: {
          font: {
            size: 14,
            family: 'Inter, sans-serif',
          },
          color: '#374151', // Gray-700
        },
      },
      title: {
        display: true,
        text: 'Investment Growth Over Time',
        font: {
          size: 20,
          family: 'Inter, sans-serif',
        },
        color: '#374151', // Gray-700
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            return `Future Value: $${context.parsed.y.toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 })}`;
          },
        },
        backgroundColor: 'rgba(31, 41, 55, 0.9)', // Gray-800 with opacity
        titleFont: {
          family: 'Inter, sans-serif',
          size: 16,
        },
        bodyFont: {
          family: 'Inter, sans-serif',
          size: 14,
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: 'Age',
          font: {
            size: 16,
            family: 'Inter, sans-serif',
          },
          color: '#374151', // Gray-700
        },
        ticks: {
          font: {
            size: 14,
            family: 'Inter, sans-serif',
          },
          color: '#374151', // Gray-700
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Future Value ($)',
          font: {
            size: 16,
            family: 'Inter, sans-serif',
          },
          color: '#374151', // Gray-700
        },
        ticks: {
          font: {
            size: 14,
            family: 'Inter, sans-serif',
          },
          color: '#374151', // Gray-700
          // Include a dollar sign and format number with commas, no decimal places
          callback: function (value) {
            return '$' + value.toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 });
          },
        },
      },
    },
  };

  const chartRef = useRef(null);

  const handleDownload = () => {
    if (chartRef.current === null) {
      return;
    }

    toPng(chartRef.current, { cacheBust: true })
      .then((dataUrl) => {
        const link = document.createElement('a');
        link.download = 'investment-growth-chart.png';
        link.href = dataUrl;
        link.click();
      })
      .catch((err) => {
        console.error('Failed to download chart as image:', err);
      });
  };

  return (
    <div className="my-8 px-4">
      <div className="bg-white dark:bg-gray-700 shadow-lg rounded-lg p-6">
        {/* Final Value */}
        <div className="mb-6 text-center">
          <p className="text-3xl font-bold text-gray-800 dark:text-gray-100">
            Future Value at Retirement:{' '}
            <span className="text-emerald-500 dark:text-emerald-400">
              $
              <CountUp
                end={finalValue}
                duration={2.5}
                separator=","
                decimals={0}
              />
            </span>
          </p>
        </div>

        {/* Chart Container */}
        <div className="relative h-80 sm:h-96" ref={chartRef}>
          <Line data={data} options={options} />
        </div>

        {/* Download Button */}
        <div className="mt-4 text-center">
          <button
            onClick={handleDownload}
            className="mt-2 px-4 py-2 bg-accent text-white rounded-md hover:bg-emerald-600 transition-colors duration-300 flex items-center space-x-2"
          >
            <SaveAsIcon className="h-5 w-5" />
            <span>Download Chart</span>
          </button>
        </div>

        {/* Parameters */}
        <div className="mt-6 px-2">
          <h2 className="text-2xl font-semibold text-center mb-4">Investment Parameters</h2>
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            <div className="text-center">
              <p className="text-lg font-medium text-gray-700 dark:text-gray-300">
                <strong>Annual Rate of Return:</strong> {(rateOfReturn * 100).toFixed(2)}%
              </p>
            </div>
            <div className="text-center">
              <p className="text-lg font-medium text-gray-700 dark:text-gray-300">
                <strong>Investment Period:</strong> {currentAge} to {retirementAge} years old
              </p>
            </div>
            <div className="text-center">
              <p className="text-lg font-medium text-gray-700 dark:text-gray-300">
                <strong>Initial Investment:</strong> ${spendingAmount.toLocaleString(undefined, { minimumFractionDigits: 0, maximumFractionDigits: 0 })}
              </p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Results;
