// src/App.js

import React, { useState } from 'react';
import Navbar from './components/Navbar';
import InputForm from './components/InputForm';
import Results from './components/Results';
import Card from './components/Card';

function App() {
  const [entry, setEntry] = useState(null);

  const handleAddEntry = (newEntry) => {
    setEntry(newEntry);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Navbar */}
      <Navbar />

      {/* Main Content */}
      <main className="flex-grow max-w-4xl mx-auto px-4">
        {/* Input Form */}
        <Card className="mb-8">
          <InputForm onAddEntry={handleAddEntry} />
        </Card>

        {/* Results */}
        {entry && (
          <Card>
            <Results entry={entry} />
          </Card>
        )}
      </main>

      {/* Footer */}
      <footer className="w-full bg-white shadow-md mt-8 rounded-t-lg">
        <div className="max-w-4xl mx-auto px-4 py-4 flex flex-col sm:flex-row justify-center items-center space-y-4 sm:space-y-0 sm:space-x-4">
          {/* Made by Collin Collins */}
          <span className="text-gray-500 text-sm">Made by Collin Collins</span>

          {/* Buy Me a Coffee Button */}
          <a
            href="https://www.buymeacoffee.com/B4Aaol3SrI"
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src="https://cdn.buymeacoffee.com/buttons/v2/default-yellow.png"
              alt="Buy Me A $3 Coffee"
              className="h-[60px] w-[217px]"
            />
          </a>
        </div>
      </footer>
    </div>
  );
}

export default App;
