// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import InputForm from './InputForm';
import Results from './Results';
import ExportData from './ExportData';
import { v4 as uuidv4 } from 'uuid';

function Dashboard() {
  const [entries, setEntries] = useState([]);

  // Load entries from localStorage on component mount
  useEffect(() => {
    const storedEntries = JSON.parse(localStorage.getItem('spendingEntries')) || [];
    setEntries(storedEntries);
  }, []);

  // Add new entry and update localStorage
  const addEntry = (entry) => {
    const newEntry = { id: uuidv4(), ...entry };
    const updatedEntries = [...entries, newEntry];
    setEntries(updatedEntries);
    localStorage.setItem('spendingEntries', JSON.stringify(updatedEntries));
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-6">Investment Calculator</h1>
      <InputForm onAddEntry={addEntry} />
      <Results entries={entries} />
      <ExportData entries={entries} />
    </div>
  );
}

export default Dashboard;
