// src/App.js

import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import InputForm from './components/InputForm';
import Results from './components/Results';
import Card from './components/Card';

function App() {
  const [entry, setEntry] = useState(null);
  const [showCoffeeButton, setShowCoffeeButton] = useState(false);
  const [threeDollarsFutureValue, setThreeDollarsFutureValue] = useState(0);

  const handleAddEntry = (newEntry) => {
    setEntry(newEntry);
    setShowCoffeeButton(true);
    calculateThreeDollarsFutureValue(newEntry.retirementAge - newEntry.currentAge);
  };

  const calculateThreeDollarsFutureValue = (years) => {
    const P = 3; // Principal amount ($3)
    const r = 0.08; // Annual interest rate (8%)
    const n = years; // Number of years

    // Future Value of an Ordinary Annuity: FV = P * [(1 + r)^n - 1] / r
    const FV = P * ((Math.pow(1 + r, n) - 1) / r);
    setThreeDollarsFutureValue(FV.toFixed(2));
  };

  useEffect(() => {
    if (showCoffeeButton) {
      // Check if the script is already present
      if (!document.getElementById('buy-me-a-coffee-script')) {
        const script = document.createElement('script');
        script.id = 'buy-me-a-coffee-script';
        script.type = 'text/javascript';
        script.src = 'https://cdnjs.buymeacoffee.com/1.0.0/button.prod.min.js';
        script.setAttribute('data-name', 'bmc-button');
        script.setAttribute('data-slug', 'B4Aaol3SrI');
        script.setAttribute('data-color', '#FFDD00');
        script.setAttribute('data-emoji', '☕');
        script.setAttribute('data-font', 'Cookie');
        script.setAttribute('data-text', 'Buy me $3* coffee!');
        script.setAttribute('data-outline-color', '#000000');
        script.setAttribute('data-font-color', '#000000');
        script.setAttribute('data-coffee-color', '#ffffff');
        script.async = true;
        document.body.appendChild(script);
      }
    }
  }, [showCoffeeButton]);

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      {/* Navbar */}
      <Navbar />

      {/* Main Content */}
      <main className="flex-grow max-w-4xl mx-auto px-4">
        {/* Input Form */}
        <Card className="mb-8">
          <InputForm onAddEntry={handleAddEntry} />
        </Card>

        {/* Results */}
        {entry && (
          <Card>
            <Results entry={entry} />
          </Card>
        )}
      </main>

      {/* Footer */}
      <footer className="w-full bg-white shadow-md mt-8 rounded-t-lg">
        <div className="max-w-4xl mx-auto px-4 py-4 flex flex-col items-center space-y-4">
          {/* Made by Collin Collins */}
          <span className="text-gray-500 text-sm">Made by Collin Collins</span>

          {/* "Buy Me a Coffee" Button */}
          {showCoffeeButton && (
            <>
              {/* Container for the Buy Me a Coffee Button */}
              <div id="buy-me-a-coffee-button" className="w-full flex justify-center"></div>

              {/* Descriptive Text */}
              <span className="text-gray-500 text-sm italic">
                *or don't and have ${threeDollarsFutureValue} in retirement
              </span>
            </>
          )}
        </div>
      </footer>
    </div>
  );
}

export default App;
