// src/components/Results.js
import React from 'react';
import { Bar } from 'react-chartjs-2';
import { calculateFutureValue } from '../utils/calculations';

// Import Chart.js and required components
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register the components
ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function Results({ entries }) {
  if (entries.length === 0) {
    return <p className="text-center">No entries yet. Start by adding your spending amounts!</p>;
  }

  const data = {
    labels: entries.map((entry, index) => `Entry ${index + 1}`),
    datasets: [
      {
        label: 'Future Value ($)',
        data: entries.map((entry) =>
          calculateFutureValue(
            entry.spendingAmount,
            0.08,
            entry.retirementAge - entry.currentAge
          ).toFixed(2)
        ),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Investment Projections',
      },
    },
    scales: {
      x: {
        type: 'category',
        title: {
          display: true,
          text: 'Entries',
        },
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Future Value ($)',
        },
      },
    },
  };

  return (
    <div className="my-8">
      <Bar data={data} options={options} />
      <ul className="mt-4">
        {entries.map((entry, index) => {
          const futureValue = calculateFutureValue(
            entry.spendingAmount,
            0.08,
            entry.retirementAge - entry.currentAge
          ).toFixed(2);
          return (
            <li key={index} className="mb-2">
              <strong>Entry {index + 1}:</strong> $
              {entry.spendingAmount} invested from age {entry.currentAge} to {entry.retirementAge} grows to $
              {futureValue}
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default Results;
