// src/App.js

import React, { useState, useEffect } from 'react';
import Navbar from './components/Navbar';
import InputForm from './components/InputForm';
import Results from './components/Results';
import Card from './components/Card';

function App() {
  const [entry, setEntry] = useState(null);

  const handleAddEntry = (newEntry) => {
    setEntry(newEntry);
  };

  useEffect(() => {
    // Dynamically load the Buy Me a Coffee script
    const script = document.createElement('script');
    script.src = 'https://cdnjs.buymeacoffee.com/1.0.0/button.prod.min.js';
    script.setAttribute('data-name', 'bmc-button');
    script.setAttribute('data-slug', 'B4Aaol3SrI');
    script.setAttribute('data-color', '#FFDD00');
    script.setAttribute('data-emoji', '☕');
    script.setAttribute('data-font', 'Cookie');
    script.setAttribute('data-text', 'Buy me a coffee!');
    script.setAttribute('data-outline-color', '#000000');
    script.setAttribute('data-font-color', '#000000');
    script.setAttribute('data-coffee-color', '#ffffff');
    script.async = true;

    document.body.appendChild(script);

    // Cleanup function to remove the script when the component unmounts
    return () => {
      document.body.removeChild(script);
    };
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col">
      <Navbar />
      <main className="flex-grow max-w-4xl mx-auto px-4">
        <Card className="mb-8">
          <InputForm onAddEntry={handleAddEntry} />
        </Card>
        {entry && (
          <Card>
            <Results entry={entry} />
          </Card>
        )}
      </main>
      <footer className="w-full bg-white shadow-md mt-8 rounded-t-lg">
        <div className="max-w-4xl mx-auto px-4 py-4 flex justify-center items-center space-x-4">
          <span className="text-gray-500 text-sm">Made by Collin Collins</span>
          {/* Buy Me a Coffee Button */}
          <a
            href="https://www.buymeacoffee.com/B4Aaol3SrI"
            target="_blank"
            rel="noopener noreferrer"
            className="inline-flex items-center justify-center px-4 py-2 bg-yellow-400 text-black font-semibold rounded-md shadow hover:bg-yellow-500 transition-colors duration-300"
          >
            <span>Buy me a coffee!</span>
          </a>
        </div>
      </footer>
    </div>
  );
}

export default App;
